INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (1, 'encanador', 'É um profissional na área da construção civil responsável pela instalação e manutenção de sistemas usados para água potável, esgoto e drenagem em sistemas de encanamento.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (2, 'pintor', 'É o profissional responsável pela proteção e decoração de paredes e outras superfícies mediante aplicação de tinta. A aplicação de tintas na construção, têm como proposito melhorar os aspetos estéticos dos edifícios, e a sua proteção contra os efeitos da água, corrosão, insetos e fungos (bolores).');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (3, 'pedreiro', 'É o profissional que constrói ou reveste muros, paredes, escadas, vigas, lajes, tectos, telhados, chaminés, etc., em edifícios, infraestruturas de saneamento e outras obras de construção geralmente orientado pelo engenheiro ou mestre de obras, utilizando materiais tais como a rocha ou pedra, o tijolo, a telha, o mosaico e o azulejo, o adobe, o cimento, a argamassa, a cal, o gesso e o betão e como ferramentas o escopro (ou cinzel) e o martelo, a picareta, a colher de pedreiro, a trolha, as réguas e esquadros, o compasso, o fio de prumo (ou prumo) e o nível de bolha (ou nível), entre outras.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (4, 'montador', 'É o profissional que monta e desmonta armários, camas, escrivaninhas, estantes e etc.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (5, 'decorador', 'É uma técnica cenográfica e visual para a composição e decoração de ambientes internos (cômodos de casas, escritórios, palácios etc.). Consiste na arte de planejar e organizar espaços, escolhendo e/ou combinando os diversos elementos de um ambiente, estabelecendo relações estéticas e funcionais, em relação ao que se pretende produzir. O profissional harmoniza, em um determinado espaço, móveis, objetos e acessórios, como cortinas e tapetes, procurando conciliar conforto, praticidade e beleza. Planeja cores, materiais, acabamentos e iluminação, utilizando tudo de acordo com o ambiente e adequando o projeto às necessidades, ao gosto e à disponibilidade financeira, do cliente.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (6, 'eletricista', 'É o especialista em instalações elétricas residenciais e industriais. Eletricistas podem ser empregados em instalação de novas componentes elétricas ou em manutenção e reparação de infra-estruturas elétricas existentes. Fazem troca de resistência elétrica, instalação de tomada de energia, interuptores e disjuntores.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (7, 'letrista', 'Elaboram e produzem painéis, cartazes, faixas e placas; recuperam placas e letreiros.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (8, 'informatica', 'É o profissional que está apto a realizar configurações de sistemas informáticos, a instalar equipamentos e a verificar as causas de falhas na programação de computadores.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (9, 'professor', 'É uma pessoa que ensina ciência, arte, técnica ou outros conhecimentos. Para o exercício dessa profissão, requer-se qualificações académicas e pedagógicas, para que consiga transmitir/ensinar a matéria de estudo da melhor forma possível ao aluno.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (10, 'diarista', 'É a pessoa que realiza trabalhos domésticos como a faxina, que é a limpeza da casa e inclui varrer, tirar o pó, lavar, retirar o lixo e outras.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (11, 'dry wall', 'O drywall, pode ser definido como um sistema de construção a seco composto por perfis, tratamento acústico (lã mineral – Isover) e placas de gesso inventadas em 1916 nos Estados Unidos e consiste em uma placa de gesso pré-fabricada, encapada com papelão ou fibra de vidro e que pode ser fixada em estruturas de  aço galvanizado para construção de casas e até mesmo prédios.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (12, 'despachante', 'É o profissional encarregado por conta de outrem de fazer requerimentos, encaminhamentos e dar início a trâmites burocráticos junto a órgãos da administração pública.');
INSERT INTO `negocio` (`negocio_id`, `negocio_nome`, `negocio_descricao`) VALUES (13, 'vidraceiro', 'Cortar, montar e instalar vidros e espelhos em portas, janelas, divisórias, vitrines, prateleiras, móveis e veículos.');
