INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (1, 'Encanador Residencial Básico', 'O objetivo deste curso grátis é de capacitar profissionais com os conhecimentos fundamentais sobre a instalação de sistemas prediais de águas fria e quente, bem como também sistemas de esgoto e pluvial, de acordo com as normas técnicas e legislação brasileira em vigor, sempre visando as condições de qualidade e segurança.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (2, 'Instalador Hidráulico', 'Executar e manter as instalações hidrossanitárias em edificações atendendo aos requisitos técnicos, de qualidade, de saúde, higiene e segurança, e de meio ambiente.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (3, 'Encanador Residencial', 'Funções e Atividades de um Encanador Conceitos Fundamentais de: Força, Pressão e Perda de Carga Ferramentas mais Utilizadas pelo Encanador Funcionamento do Sistema Predial de Água Fria Dimensionamento das Instalações de Água Fria Dimensionamento das Tubulações de Água Fria Instalações – Embutidas, Aparentes e Enterradas Instalações – Transposições e Variáveis Térmicas');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (4, 'Pintor de obras imobiliárias', 'Executar pintura de obras imobiliárias, seguindo normas técnicas, de qualidade, de saúde, de segurança, meio ambiente e procedimentos técnicos.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (5, 'Curso Pintor de Obras', 'Neste curso de pintor de obras você aprenderá a realizar pintura em paredes internas e externas; preparar as superfícies antes de pintá-las como limpeza, aplicação de massa fina ou corrida e lixamento, além de combinar cores, texturas, aplicações em diversas superfícies como madeira, tijolos, alvenaria em geral, aplicação de massa corrida e normas de segurança física e do meio ambiente, como também às especificações dos fabricantes.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (6, 'Curso de Pintor Profissional', 'O Curso a distância de Pintor foi desenvolvido em parceria com empresas especialistas da área como tintas Coral, verniz Sparlack, pincéis Tigre e Escadas Segurança. Nele você aprenderá o essencial para se qualificar profissionalmente para o mercado de trabalho.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (7, 'Curso de Pedreiro', 'Matricule-se no Curso a distância de Pedreiro e conheça todas as fases de mão de obra que envolve uma construção. Aprenda, por meio de apostilas ilustradas, a ler e interpretar plantas e projetos arquitetônicos, calcular áreas e volume, conhecer os materiais utilizados durante uma construção, preparar o canteiro de obras e muito mais.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (8, 'Pedreiro de Alvenaria', 'Construir alvenarias com e sem função estrutural, seguindo normas técnicas, de qualidade, de saúde, de segurança, meio ambiente e procedimentos técnicos');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (9, 'Montador e Instalador de Móveis', 'Instalar móveis, respeitando procedimentos e normas técnicas, ambientais, de qualidade, de saúde e de segurança.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (10, 'Curso de Montador de Móveis', 'O Curso de Montador de Móveis abrange conceitos de leitura e interpretação simples de projetos e esquemas de montagem de móveis, como também para realizar pequenos reparos e reposição de peças.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (11, 'Design de Interiores', ' Durante o curso, os/as estudantes exercitam a busca de soluções em espaços pré-concebidos, aplicando em nível de projeto, a adequação de novas atividades que visam transformar ou melhorar a qualidade de vida nos ambientes comerciais, residenciais, design de iluminação, jardins ou espaços efêmeros.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (12, 'Curso de Eletricista', 'Você vai aprender a fazer todos os reparos em rede elétrica, ou seja: identificação de defeitos, instalação de tomada, instalação de interruptor, instalação  disjuntor, cabeamento da rede elétrica.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (13, 'Curso de Letrista e Cartazista', 'O curso online Letrista e cartazista aborda conceitos sobre os materiais utilizados pelo cartazista, bem como, sobre a evolução dos cartazistas face à história, como fazer um cartaz, cartazes que chamam a atenção, cartazes cubanos, a letra no cartaz, manual de letras: família, geometria e proporções das letras.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (14, 'Curso de Informática', 'Curso Técnico em Informática é totalmente focado na atuação imediata no mercado de trabalho. Nele, você desenvolverá competências para realizar planejamento e execução dos processos de manutenção de computadores (tanto hardware quanto software), operação de redes locais de computadores, além do desenvolvimento, teste e manutenção de aplicativos para desktops, dispositivos móveis e internet.');
INSERT INTO `curso` (`curso_id`, `curso_nome`, `curso_descricao`) VALUES (15, 'Curso de Alfabetização', 'o Estude sem Fronteiras aprofundará os conceitos de alfabetização e letramento e as relações intrínsecas entre estas duas terminologias que perpassam as discussões entre professores e profissionais da educação');
